import React from "react";
import {
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Container,
} from "@material-ui/core";
import {Field} from "formik";

const Step1 = (props) => {
  if (props.currentStep !== 1) return null;
  return (
    <Container maxWidth='md'>
      <InputLabel>Duration:</InputLabel>
      <Field
        component={Select}
        fullWidth
        name='duration'
        type='number'
        defaultValue={props.values.duration}>
        <MenuItem value={3}>3 Months</MenuItem>
        <MenuItem value={6}>6 Months</MenuItem>
        <MenuItem value={12}>12 Months</MenuItem>
      </Field>

      <InputLabel>Amount of gigabytes in a cloud:</InputLabel>
      <Field
        component={Select}
        fullWidth
        name='gigabytes'
        type='number'
        defaultValue={props.values.gigabytes}>
        <MenuItem value={3}>3 GB</MenuItem>
        <MenuItem value={5}>5 GB</MenuItem>
        <MenuItem value={10}>10 GB</MenuItem>
        <MenuItem value={20}>20 GB</MenuItem>
        <MenuItem value={30}>30 GB</MenuItem>
        <MenuItem value={50}>50 GB</MenuItem>
      </Field>

      <InputLabel>Upfront payment:</InputLabel>
      <Field
        component={Select}
        fullWidth
        name='duration'
        type={Boolean}
        defaultValue={props.values.upfrontPayment}>
        <MenuItem value={false}>No</MenuItem>
        <MenuItem value={true}>Yes</MenuItem>
      </Field>
    </Container>
  );
};

export default Step1;
